select			*
from			@{variables('ODB_OWNER_NAME')}.@{variables('Table_OE_ORDER_LINES_ALL')}
where			LAST_UPDATE_DATE >= '@{formatDateTime(subtractFromTime(activity('Lookup SQL OE_ORDER_LINES_ALL Watermark').output.FirstRow.WATERMARK_DATETIME, 1, 'Day'), 'dd/MMM/yy')}'

select			max(cast(LAST_UPDATE_DATE as timestamp)) as LAST_UPDATE_DATE
from			@{variables('ODB_OWNER_NAME')}.@{variables('Table_OE_ORDER_LINES_ALL')}
where LAST_UPDATE_DATE >= '@{getPastTime(120, 'Day', 'dd/MMM/yy')}'
